div.script:
var resp = httpGet('https://raw.githubusercontent.com/mjc-school/MJC-School/stage0/stage%20%230/module%20%231.%20Java%20Overview/content.md')

var el = document.getElementsByName('textarea')
el[0].innerHTML = resp

function httpGet(theUrl)
{
	var xmlHttp = new XMLHttpRequest();
	xmlHttp.open( "GET", theUrl, false ); // false for synchronous request
	xmlHttp.send( null );
	return xmlHttp.responseText;
}
